    team = result["data"]["teams"]["nodes"][0]
    members_with_counts = []

    for member in team["members"]["nodes"]:
        issue_count = len(member["assignedIssues"]["nodes"])
        members_with_counts.append({
            "id": member["id"],
            "name": member["name"],
            "issue_count": issue_count
        })

    print("\n\nEngineering Team Members and their assigned issue counts:")
    for member in sorted(members_with_counts, key=lambda x: x["issue_count"]):
        print(f"  {member['name']}: {member['issue_count']} issues (ID: {member['id']})")

    # Find the member with the fewest issues
    min_count = min(m["issue_count"] for m in members_with_counts)
    candidates = [m for m in members_with_counts if m["issue_count"] == min_count]

    print(f"\n\nMembers with fewest issues ({min_count}):")
    for candidate in candidates:
        print(f"  {candidate['name']} (ID: {candidate['id']})")

    # Pick the first one if there are ties
    selected = candidates[0]
    print(f"\n\nSelected: {selected['name']} (ID: {selected['id']})")
