# Get all users and filter Engineering team members by their assigned issues
query_users = """
query {
  users(first: 100) {
    nodes {
      id
      name
      teams {
        nodes {
          name
        }
      }
      assignedIssues(first: 100) {
        nodes {
          id
        }
      }
    }
  }
}
"""

