# Count assignments per team member
if "data" in all_issues_data and all_issues_data["data"]["issues"]["nodes"]:
    assignee_counts = {}
    email_signin_issue_id = None

    for issue in all_issues_data["data"]["issues"]["nodes"]:
        if "email sign-in" in issue["title"].lower():
            email_signin_issue_id = issue["id"]
            print(f"\nFound 'email sign-in' issue: {issue['title']} (ID: {issue['id']})")

        if issue["assignee"]:
            assignee_id = issue["assignee"]["id"]
            assignee_name = issue["assignee"]["name"]
            if assignee_id not in assignee_counts:
                assignee_counts[assignee_id] = {"name": assignee_name, "count": 0}
            assignee_counts[assignee_id]["count"] += 1

    print("\nAssignment counts per team member:")
    for user_id, info in sorted(assignee_counts.items(), key=lambda x: x[1]["count"]):
        print(f"  {info['name']}: {info['count']} issues")

    # Find team member with fewest assignments
    min_count = min(info["count"] for info in assignee_counts.values())
    members_with_min = [user_id for user_id, info in assignee_counts.items() if info["count"] == min_count]

    # Pick the first one if there's a tie
    selected_member_id = members_with_min[0]
    selected_member_name = assignee_counts[selected_member_id]["name"]

    print(f"\nSelected member with fewest assignments: {selected_member_name} ({selected_member_id})")
    print(f"Email sign-in issue ID: {email_signin_issue_id}")
