# Linear GraphQL API endpoint
url = "https://api.linear.app/graphql"

# Create the three issues
issues_to_create = [
    {"title": "Alpha"},
    {"title": "Beta"},
    {"title": "Gamma"}
]

issue_ids = {}

for issue in issues_to_create:
    query = """
    mutation CreateIssue($input: IssueCreateInput!) {
      issueCreate(input: $input) {
        issue {
          id
          identifier
          title
        }
        success
      }
    }
    """

