    if response.json().get("data", {}).get("issueCreate"):
        issue_id = response.json()["data"]["issueCreate"]["issue"]["id"]
        identifier = response.json()["data"]["issueCreate"]["issue"]["identifier"]
        issue_ids[issue["title"]] = {"id": issue_id, "identifier": identifier}
        print(f"✓ Created {identifier}\n")
    else:
